mapper.select:
	
	 
     select C.*
       from (
  	 select rownum as rnum, B.*
  	   from (
  	 select *
  	   from notice_board
   	  order by id desc ) B
  	  where rownum <= #{end} ) C
  	  where C.rnum >= #{start}

mapper.update:
  UPDATE NOTICE_BOARD SET
  	   SUBJECT = #{subject}
  	 , CONTENTS = #{contents}
	 WHERE ID = #{id}
